package library.maths;import java.io.*;// class that describes a number with a variancepublic class MeanSigmaimplements Serializable{	private static final long serialVersionUID = -5418644551419691280L;		public double mean, sigma;		// constructor that generates a number with an error	public MeanSigma(double mean, double sigma)	{		this.mean  = mean;		this.sigma = sigma;	}	// constructor that copies a number with an error	public MeanSigma(MeanSigma ms)	{		this.mean  = ms.mean;		this.sigma = ms.sigma;	}		// constructor that creates a number zero error	public MeanSigma(double mean)	{		this.mean  = mean;		this.sigma = 0;	}		public String toString()	{		return "(" + mean + " +/- " + sigma + ")";	}}